INSERT INTO public.ristorante (fk_esercente, nome, indirizzo, descrizione, punteggio,foto,coordinate) VALUES
         ('Giova', 'Il Pozzo Spagnolo', 'Via Fusandola 1/3', 'Consegna a domicilio, Da asporto, Prenotazioni online, Disponibilità di seggiolini da tavolo per bambini, Cucina italiana, Pesce, Cucina mediterranea, Cucina spagnola', 90, 'https://qul.imgix.net/9b49b845-3c3b-47c6-be65-ace7b9ba5aba/375939_sld.jpg', '4068016, 14753482'),
         ('Giova', 'Himiko', 'Via Roma 268', 'La nostra filosofia di vita é in diretta armonia con quella giapponese: ricerca della perfezione estetica e di gusti. Bellezza, rigore formale e sensibilitá estetica sono radicate nella tradizione.', 90, 'https://img.grouponcdn.com/deal/3g6b9SD9LxL85rg4VpDJQ4Vos7ed/3g-793x476/v1/c870x524.jpg', '406790015, 147544048'),
         ('Giova', 'Terraferma', 'Via Roma 270', 'rimanendo con i piedi fermi e stabili sulla terra, ma con la voglia di guardare lontano, verso l’infinito di quel mare che bagna le coste di una città che ha fatto della pesca parte integrante della sua storia.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/1d/83/b3/39/locale-moderno-con-una.jpg', '406790018, 147544026'),
         ('Giova', 'Anima e Cornicione', 'Via Portacatena 42', 'Anima & Cornicione è una pizzeria di Salerno, aperta nel 2020 da Agostino Pane. La pizza è quella classica napoletana mutuata dalle tante esperienze di Agostino nei locali italiani ed esteri.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/1b/9d/48/19/caption.jpg', '40679496, 14754712'),
         ('Giova', 'Gli Esposito', 'Via Roma 238', 'Salerno è un’isola felice per la ristorazione fuori casa senza glutine e a questa oasi sglutinata si aggiunge una chicca che sarà presto invidiata da chi vive lontano da questa città: la pizzeria Gli Esposito.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/14/ca/dc/f5/pignalosa-pizzeria-location.jpg', '406790942, 14755491'),
         ('Giova', 'Ristorante Mimmo e Lucio', 'Via Roma 264', 'Ristorante storico situato in una delle vie più centrali e frequentate di Salerno. Si distingue per la sua cucina semplice e casereccia.', 90, 'https://menu.sluurpy.it/foto-g/125629/3855713.jpg', '40679001, 147544092'),
         ('Giova', 'Galleon Pub Pizzeria', 'Via Roma 254', 'Il locale occupa una posizione centralissima nella città di Salerno, a pochi passi dal lungomare e vicino a luoghi istituzionali quali la sede del Comune, il palazzo della Provincia, la Prefettura e altri posti di interesse anche di carattere turistico.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/08/9b/c3/7a/galleon-pub.jpg', '40679035, 14754804'),
         ('Giova', 'Ingordo Sushi', 'Via Roma, 260', 'Un piccolo bistrot del gusto specializzato in sushi d''autore con ingredienti di primissima qualità e prodotti tipici giapponesi eccellenti.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/11/8c/f5/bd/migliore-ristorante-giapponese.jpg', '4067907, 14754429'),
         ('Giova', 'Ristorante Volte al Mare', 'Via Roma, 282-286', 'Alle porte del centro storico di  Salerno,  si aprono le volte del Ristorante.Una "trattoria e pizzeria", che vuole portare in tavola i piatti  della tradizione salernitana e campana rivisitati in chive moderna.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/28/65/e9/37/la-sala-interna.jpg', '406790884, 147542973'),
         ('Giova', 'Mini Pub 2.0', 'Piazza Matteo Luciani', 'Piatti tipici della cucina salernitana, personale gentile ed ospitale, servizio veloce', 90, 'https://media-cdn.tripadvisor.com/media/photo-l/2a/ca/c1/e0/caption.jpg', '406791665, 147527216'),
         ('Giova', 'Lola Wine Food & Tartare', 'Via Portacatena 1', 'Cucina italiana, Cucina peruviana, Pesce, Cucina sudamericana', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/1d/86/b4/bb/alici-imbottite-con-pickle.jpg', '40679547, 14755647'),
         ('Giova', 'Acqvamarina', 'Piazza Matteo Luciani 26', 'ACQVAMARINA è il ristorante di mare al Centro di Salerno, a pochissimi passi dal Teatro Verdi. Una cucina tradizionale di pesce con un pizzico di innovazione.', 90, 'https://img.restaurantguru.com/rf4e-Acqvamarina-Ristorante-di-Mare-design.jpg', '40679534, 1475261'),
         ('Giova', 'O''Cuzztiello', 'Via Roma 274', 'O'' Cuzzetiello arriva a Salerno... ma cos''è il Cuzzetiello? E'' la parte più sfiziosa del pane cafone, l''estremità dalla consistenza croccante e dalla forma tondeggiante farcito secondo la tradizione.', 90, 'https://media-cdn.tripadvisor.com/media/photo-p/19/e9/92/61/getlstd-property-photo.jpg', '406790024, 147543983'),
         ('Giova', 'Toc Toc', 'Via Porta di Mare 21', 'Toc Toc è sorpresa, gusto e divertimento alla portata di tutti. Primissima qualità per deliziare il vostro palato sotto forma di panini scostumati, di primi e secondi sempre diversi!', 90, 'https://res.cloudinary.com/tf-lab/image/upload/f_auto,q_auto,w_664,h_374/restaurant/f22d07ee-8ad8-4a06-9118-93ac0eb06a6b/c371d7cb-5f58-4818-9b6e-eee6f0168f6f.jpg', '40679251, 14756766'),
         ('Giova', 'Dodici Ventiquattro', 'Via Roma, 170', 'Ristorante molto accogliente, personale molto gentile e ottimo cibo.', 90, 'https://res.cloudinary.com/tf-lab/image/upload/f_auto,q_auto,w_664,h_374/restaurant/c0ce0ae3-c7e4-4d4f-8c00-30888a52b513/c514866c-e545-466b-8d38-fff4fc73cc88.jpg', '406788566, 147571194'),
         ('Giova', 'Baylon', 'Piazza Sedile del Campo, 6 Largo Campo', 'Sei alla ricerca del locale perfetto per le tue serate con gli amici? Il Pub Baylon il panino del Professore è il posto perfetto dove trascorrere del tempo in compagnia.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/13/26/17/b2/baylon.jpg', '40679862, 14755866'),
         ('Giova', 'Paninoteca Sant''Andrea', 'Piazza Sedile del Campo 13', 'Da sempre il Sant’Andrea segue la cucina tradizionale, dando però un tocco moderno ai nostri panini e piatti.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/0d/91/21/1b/sant-andrea-panineria.jpg', '40679673, 147560978'),
         ('Giova', 'Osteria Del Campo', 'Via Porta Catena 1 Largo Campo', 'Il ristorante propone oltre ad ottime pizze piatti tipici salernitani. È aperto tutti i giorni sia a pranzo che a cena tranne il martedì che è il giorno di chiusura.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/10/ef/ab/9d/osteria-del-campo.jpg', '41775489, 12921811'),
         ('Giova', 'Resilienza', 'Via Sabatini, 4', 'Resilienza nasce come Pizzeria di qualità nel 2013 a Salerno. La filosofia del km0, del biologico e del buono pulito e giusto riscuote subito successo.', 90, 'https://www.scattidigusto.it/wp-content/uploads/2021/01/pizzeria-Resilienza-Salerno-insegna-1280x1707.jpg', '40678355, 147500023'),
         ('Giova', 'Ristorante Sant''Andrea', 'Piazza Sedile del Campo, 13', 'Italiana, Pizza, Pesce, Mediterranea, Napoletana, Campana, Italiana (sud)', 90, 'https://salerno.esn.it/sites/esnsalerno.it/files/partners/images/ristorante_santandrea.png', '40679673, 147560978'),
         ('Giova', 'Bar Umberto Salerno', 'Via Roma 248', 'Il Bar Umberto è in via Roma, nel cuore del centro di Salerno.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/0e/a4/98/c6/photo1jpg.jpg', '40679019, 14754972'),
         ('Giova', 'King''s Cross', 'Via Roma, 214', 'Situato nel cuore del centro storico di Salerno, il pub ristorante King''s Cross ti accoglie con deliziose specialità tipiche abbinate a una vasta selezione di birre di alta qualità.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/08/ec/09/0c/king-s-cross-irish-pub.jpg', '406788605, 147557408'),
         ('Giova', 'Ristorante Classico Salernitano', 'Via Portacatena, 1', 'Entrare nel Ristorante Tipico Salernitano significa fare un viaggio nella tradizione locale e mangiare la vera cucina di Salerno sia di mare che di terra.', 90, 'https://ristoranteclassicosalernitano.com/assets/img/web_2.jpg', '40679547, 14755647'),
         ('Giova', 'Da Nonno Raffaele', 'Vicolo Porta Rateprandi,19', 'Da Nonno Raffaele è un locale molto frequentato nella zona di Centro Storico. Che tu sia alla ricerca di un pasto leggero o di un''esperienza gastronomica completa, esplora i piatti del Da Nonno Raffaele e sperimenta l''autentica cucina Italiana di Salerno.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/18/69/90/24/banco.jpg', '406803035, 147559363'),
         ('Giova', 'Botteghelle65', 'Via delle botteghelle 65', 'Salumeria storica con cucina e servizio di piccola ristorazione. La bottega è stata fondata nel 1919 dalla famiglia Conte, successivamente grazie al Maestro degustatore Pino Adinolfi si è posizionata fin da subito con prodotti di eccellenza locali e regionali, con particolare attenzione ai prodotti Slow-Food, BIO e con piatti di tipica cucina Salernitana.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/09/94/81/69/botteghelle65.jpg', '40680625, 147588878'),
         ('Giova', 'Ingordo', 'Via Roma 184', 'Molto più di un semplice burger, Ingordo è un esperienza di gusto e divertimento! I nostri prodotti sono scelti e selezionati accuratamente, tanto da rendere il nostri burger un’esperienza esclusiva!', 90, 'https://media-cdn.tripadvisor.com/media/photo-p/0e/4d/60/67/ingordo-best-burger-salerno.jpg', '406790093, 14754356'),
         ('Giova', 'Paninoteca da Pippo', 'Piazza Sedile del Campo 1', 'Ottimo kebab con prezzi davvero bassi per essere nel pieno centro storico di Salerno. Pippo e lo staff sono molto accoglienti e cordiali.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/1a/7b/01/8b/paninis-y-kabak.jpg', '406795011, 147559563'),
         ('Giova', 'Ristorante del centro storico', 'Via Roma, 208', 'Ristorante del Centro Storico si trova a Salerno, in via Roma al numero 208, è una trattoria tipica che offre un ampio assortimento di piatti di cucina mediterranea appositamente rivisitati. Ci occupiamo principalmente della preparazione di piatti a base di pesce fresco, ma ci sono anche tante specialità realizzate con carni selezionate presso i migliori allevamenti.', 90, 'https://media-cdn.tripadvisor.com/media/photo-s/06/71/1d/a0/logo_rotated_90.jpg', '406790093, 14754356');

