INSERT INTO public.tappa (id, descrizione, nome, coordinate,foto) VALUES
 (nextval('tappa_id_seq'), 'Salerno è una città ricca di storia e cultura, e il Castello Arechi è uno dei suoi monumenti più importanti. Il castello si trova sulla cima del Monte Bonadies, a circa 300 metri sul livello del mare, e offre una vista panoramica mozzafiato della città e del Golfo di Salerno.', 'Castello Arechi', '40685584, 14755099', '/images/castello-arechi.jpg'),
 (nextval('tappa_id_seq'), 'Salerno è una città ricca di storia e cultura, e la Via dei Mercanti è una delle sue strade più antiche e affascinanti. La strada si trova nel centro storico della città, ed è un importante centro commerciale e culturale.', 'Via dei Mercanti', '40679020, 14759893', '/images/via-dei-mercanti.jpg'),
 (nextval('tappa_id_seq'), 'La Cattedrale di Salerno è uno dei monumenti più importanti della città. La chiesa è un capolavoro dell''architettura arabo-normanna, e ospita importanti opere d''arte, tra cui il mosaico di San Matteo.', 'Duomo di Salerno', '40680351, 14759387', '/images/duomo.jpg'),
 (nextval('tappa_id_seq'), 'Il Giardino della Minerva è un giardino botanico situato nel centro storico di Salerno. Il giardino è uno dei più antichi giardini botanici d''Italia, e ospita una ricca collezione di piante medicinali.', 'Giardino della minerva', '40681018, 14753851', '/images/giardino-minerva.jpg'),
 (nextval('tappa_id_seq'), 'Il Complesso Monumentale di Santa Sofia è un complesso religioso e culturale situato nel centro storico di Salerno ed è costituito da una chiesa, un monastero e un museo', 'Complesso monumentale di Santa Sofia', '40680665, 14757648', '/images/santa-sofia.jpg'),
 (nextval('tappa_id_seq'), 'Il Museo Diocesano di Salerno è un museo d''arte sacra situato nel centro storico della città e ospita una collezione di opere d''arte che coprono un periodo di tempo che va dal Medioevo al XVIII secolo.', 'Museo Diocesano', '40680821, 14760240', '/images/museo-diocesano.jpg'),
 (nextval('tappa_id_seq'), 'Il Museo archeologico di Salerno è un museo situato nel centro storico della città e ospita una collezione di reperti archeologici che coprono un periodo di tempo che va dalla preistoria all''epoca romana.', 'Museo Archeologico', '40679152, 14762394', '/images/museo-archeologico.jpg'),
 (nextval('tappa_id_seq'), 'La Pinacoteca Provinciale di Salerno è un museo di arte situato nel centro storico della città. Il museo ospita una collezione di dipinti che coprono un periodo di tempo che va dal Medioevo al XIX secolo.', 'Pinacoteca provinciale', '40678968, 14760127', '/images/pinacoteca.jpg'),
 (nextval('tappa_id_seq'), 'Il Palazzo Pinto di Salerno è un palazzo storico situato nel centro storico della città. Il palazzo è uno dei più importanti esempi di architettura barocca della Campania.', 'Palazzo Pinto', '40679028, 14759889', '/images/palazzo-pinto.jpg'),
 (nextval('tappa_id_seq'), 'Il Complesso Monumentale di San Pietro a Corte è un complesso storico e religioso situato nel centro storico di Salerno. Il complesso è costituito da una chiesa, un monastero e un museo.', 'Complesso monumentale di San Pietro a corte', '40679696, 14757438', '/images/complesso-s-pietro.jpg'),
 (nextval('tappa_id_seq'), 'Il Teatro Verdi di Salerno è un teatro lirico situato nel centro storico della città. Il teatro stato inaugurato nel 1872, ed è uno dei più importanti teatri lirici della Campania.', 'Teatro Verdi', '40679222, 14752324', '/images/teatro-verdi.jpg'),
 (nextval('tappa_id_seq'), 'Il Lungomare Trieste di Salerno è un lungomare lungo circa 2 chilometri che si affaccia sul Golfo di Salerno. Il lungomare è un luogo ideale per passeggiare, fare jogging o semplicemente rilassarsi e ammirare la vista.', 'Lungomare Trieste', '40676934, 14761764', '/images/lungomare-trieste.jpg'),
 (nextval('tappa_id_seq'), 'Piazza della Libertà è una piazza situata nel centro storico di Salerno. La piazza è stata inaugurata nel 2021 e rappresenta la nuova visione della città verso un futuro sempre più orientato al turismo.', 'Piazza della Libertà', '40676541, 14753412', '/images/piazza-liberta.jpg'),
 (nextval('tappa_id_seq'), 'La stazione marittima di Salerno è una struttura moderna situata nel porto di Salerno. La stazione è stata inaugurata nel 2016 e rappresenta una nuova visione dell''architettura portuale.', 'Stazione marittima', '40674860, 14751789', '/images/stazione-marittima.jpg'),
 (nextval('tappa_id_seq'), 'Il Corso Vittorio Emanuele è la principale via dello shopping di Salerno. Il corso è un luogo vivace e animato, che ospita negozi, ristoranti e bar.', 'Corso Vittorio Emanuele', '40676865, 14766292', '/images/corso-v-emanuele.png'),

